package utc.laptrinhweb.QLBH.model;import lombok.Data;import javax.persistence.Column;import javax.persistence.Entity;import javax.persistence.Id;@Data@Entity(name = "DANHMUC")public class Category {    @Id    @Column(name = "MA_DM")    private String id;    @Column(name = "TEN")    private String name;    @Column(name = "NGAY_TAO")    private String createAt;    @Column(name = "TRANG_THAI")    private String state;    @Column(name = "NOTE")    private String note;}