package utc.laptrinhweb.QLBH.model;import lombok.Data;import javax.persistence.Column;import javax.persistence.Entity;import javax.persistence.Id;@Entity(name = "KHACHHANG")@Datapublic class Customer {    @Id    @Column(name = "MA")    private String id;    @Column(name = "TEN")    private String name;    @Column(name = "TINH_TP")    private String country;    @Column(name = "DIA_CHI")    private String countryCode;    @Column(name = "SO_DIEN_THOAI")    private String address;    @Column(name = "XA_PHUONG")    private String phoneNumber;    @Column(name = "NGAY_TAO")    private String createAt;}