package utc.laptrinhweb.QLBH.model;import lombok.Data;import javax.persistence.Column;import javax.persistence.Entity;import javax.persistence.Id;import java.util.Date;@Data@Entity(name = "HOADON")public class Order {    @Id    @Column(name = "MA_HD")    private String id;    @Column(name = "MA_KH")    private String customerId;    @Column(name = "MA_NV")    private String nhanvienId;    @Column(name = "SO_LUONG")    private int quantity;    @Column(name = "TONG_TIEN")    private Double amount;    @Column(name = "NGAY_SUA")    private Date createAt;    @Column(name = "TRANG_THAI")    private String state;}