package utc.laptrinhweb.QLBH.model;import lombok.Data;import javax.persistence.Column;import javax.persistence.Entity;import javax.persistence.Id;import java.util.Date;@Entity(name = "SANPHAM")@Datapublic class Product {    @Id    @Column(name = "MA_SP")    private String id;    @Column(name = "TEN")    private String name;    @Column(name = "NGAY_TAO")    private Date createAt;    @Column(name = "HINH_ANH")    private String image;    @Column(name = "NGAY_SUA")    private Date updateAt;    @Column(name = "SO_LUONG")    private String quantity;    @Column(name = "MA_DM")    private String dmId;    @Column(name = "MA_NCC")    private String nccId;    @Column(name = "MA_MAU")    private String colorId;    @Column(name = "MA_KT")    private String sizeId;    @Column(name = "GIA_BAN")    private double price;    @Column(name = "GIA_NHAP")    private double priceCost;}